import React, { useState, useEffect } from "react";

import styled from "styled-components";
import * as Yup from "yup";

import DeleteForeverIcon from "@material-ui/icons/DeleteForever";
import EditIcon from "@material-ui/icons/Edit";
import Button from "@material-ui/core/Button";
import Drawer from "@material-ui/core/Drawer";

import { Formik, Form, Field } from "formik";

import { Debug } from "../../../aux/Debug";
import data from "./data.js";

const Table = styled.table`
  width: 100%;
  text-align: left;
  padding: 16px 0;
`;
const HeadCell = styled.td`
  padding: 16px 0;
  width: 20%;
`;

const TableCell = styled.td`
  padding: 8px 0;
  width: 23%;
  &(:last-of-type) {
    display: flex;
    justify-content: flex-end;
    width: 8%;
  }
`;

const Amount = styled.p`
  color: ${({ type }) => (type === "expense" ? "#FF7661" : "#00E4C6")};
`;

const Container = styled.div`
  width: 100%;
  padding: 64px;
`;

const AddButtonWrapper = styled.div`
  display: flex;
  justify-content: flex-end;
`;

const FormWrapper = styled.div`
  padding: 16px;
  width: 380px;
  height: 100vh;
  overflow: scroll;
  background-color: #252f3d;
  color: white;
`;

const TransactionsList = () => {
  const [transactions, setTransactions] = useState([]);
  const [openDrawer, setOpenDrawer] = useState(true);

  useEffect(() => {
    setTransactions(data);
  }, []);

  const formatter = new Intl.NumberFormat("en-US", {
    style: "currency",
    currency: "USD",
    minimumFractionDigits: 2,
  });

  const transactionSchema = Yup.object().shape({
    name: Yup.string().required("Required field"),
    date: Yup.date()
      .default(() => new Date())
      .required("Required field"),
    amount: Yup.number().required("Required field"),
    category: Yup.string().required("Required field"),
    type: Yup.string().required("Required field"),
  });

  const handleDelete = (id) => {
    const _transactions = [...transactions].filter(
      (transaction) => transaction.id !== id
    );
    setTransactions(_transactions);
  };

  return (
    <Container>
      <AddButtonWrapper>
        <Button
          variant='contained'
          color='primary'
          onClick={() => setOpenDrawer(true)}
        >
          + Add Transaction
        </Button>
      </AddButtonWrapper>
      <Table>
        <thead>
          <tr>
            <HeadCell>Date</HeadCell>
            <HeadCell>Name</HeadCell>
            <HeadCell>Category</HeadCell>
            <HeadCell>Amount</HeadCell>
            <HeadCell></HeadCell>
          </tr>
        </thead>
        <tbody>
          {transactions.map(({ id, date, name, category, type, amount }) => {
            return (
              <tr key={id}>
                <TableCell>{date}</TableCell>
                <TableCell>{name}</TableCell>
                <TableCell>{category}</TableCell>
                <TableCell>
                  <Amount type={type}>{formatter.format(amount)}</Amount>
                </TableCell>
                <TableCell>
                  <EditIcon
                    style={{ marginRight: "16px" }}
                    onClick={(e) => {
                      console.log("I should open edit mode");
                      console.log(e, id);
                    }}
                  />
                  <DeleteForeverIcon
                    style={{ color: "#FF7661" }}
                    onClick={() => handleDelete(id)}
                  />
                </TableCell>
              </tr>
            );
          })}
        </tbody>
      </Table>

      {openDrawer && (
        <Drawer
          anchor='right'
          open={openDrawer}
          onClose={() => setOpenDrawer(false)}
        >
          <FormWrapper>
            <h2>New transaction</h2>
            <Formik
              initialValues={{
                name: "",
                amount: "",
                date: "",
                category: "eating_out",
                type: "expense",
              }}
              validationSchema={transactionSchema}
              onSubmit={(values, { setSubmitting }) => {
                setTimeout(() => {
                  alert(JSON.stringify(values, null, 2));
                  setSubmitting(false);
                }, 400);
              }}
            >
              {({ isSubmitting }) => (
                <>
                  <Form>
                    <Field name='name' />
                    <Field name='date' />
                    <Field name='amount' />
                    <Field name='category' type='radio' />

                    <button type='submit' disabled={isSubmitting}>
                      Submit
                    </button>
                  </Form>
                  <Debug />
                </>
              )}
            </Formik>
          </FormWrapper>
        </Drawer>
      )}
    </Container>
  );
};

export { TransactionsList };
